mapper.update:
UPDATE study_joined_member
SET grade=2
WHERE memno=#{memNo}
AND stdno=#{no}

mapper.select:
SELECT stdno, memno, grade
FROM study_joined_member
WHERE memno=#{memNo}
AND stdno=#{no}

mapper.update:
update study_joined_member set
rdt=now(),
grade=#{grade}
where stdno=#{studyNo} AND memno=#{memNo}
